command:example.dev:code:
$fldr = Join-Path ([Environment]::GetFolderPath('Desktop')) 'AcmeAccountKeys'
New-Item -ItemType Directory -Force -Path $fldr | Out-Null
Get-PAAccount -List | %{
    Export-PAAccountKey $_.ID -OutputFile "$fldr\$($_.ID).key" -Force
}

$pArgs = @{
    FDToken = (Read-Host 'FakeDNS API Token' -AsSecureString)
    WSHPort = 8080
}
New-PACertificate '*.example.com','example.com' -Plugin FakeDNS,WebSelfHost -PluginArgs $pArgs

$splat = @{
    Domain = 'example.com'
    Account = (Get-PAAccount)
    Token = 'fake-token'
    Plugin = 'FakeDNS'
    PluginArgs = @{
        FDToken = (Read-Host 'FakeDNS API Token' -AsSecureString)
    }
}
Publish-Challenge @splat

# if plugin requires saving
Save-Challenge -Plugin $splat.Plugin -PluginArgs $splat.PluginArgs

$splat = @{
    Account = (Get-PAAccount)
    Plugin = 'FakeDNS'
    PluginArgs = @{
        FDToken = (Read-Host 'FakeDNS API Token' -AsSecureString)
    }
}
Get-PAOrder | Get-PAAuthorization | ForEach-Object {
    Publish-Challenge -Domain $_.DNSId -Token $_.DNS01Token @splat
}

# if plugin requires saving
Save-Challenge -Plugin $splat.Plugin -PluginArgs $splat.PluginArgs

$splat = @{
    Account = (Get-PAAccount)
    Plugin = 'WebSelfHost'
    PluginArgs = @{}
}
Get-PAOrder | Get-PAAuthorization | ForEach-Object {
    Publish-Challenge -Domain $_.DNSId -Token $_.HTTP01Token @splat
}

# if plugin requires saving
Save-Challenge -Plugin $splat.Plugin -PluginArgs $splat.PluginArgs

$splat = @{
    Domain = 'example.com'
    Account = (Get-PAAccount)
    Token = 'fake-token'
    Plugin = 'FakeDNS'
    PluginArgs = @{
        FDToken = (Read-Host 'FakeDNS API Token' -AsSecureString)
    }
}
Unpublish-Challenge @splat

# if plugin requires saving
Save-Challenge -Plugin $splat.Plugin -PluginArgs $splat.PluginArgs

